import React from "react";
import { Button,Dropdown} from "antd";
// import { Menu } from 'antd';
import './../resources/defaultlayout.css'
import { Navigate } from "react-router-dom";


function DefaultLayout(props){
  const user=JSON.parse(localStorage.getItem('shaikhresume-user'))
  const items= [
    {
      key: '1',
      label: (
        <a target="_blank" rel="noopener noreferrer" href="/home">
         Home
        </a>
      ),
    },
    {
      key: '2',
      label: (
        <a target="_blank" rel="noopener noreferrer" href="/profile">
         Profile
        </a>
      ),
    },
    {
      key: '3',
      label: (
        <span onClick={()=>{
          localStorage.removeItem('shaikhresume-user')
          Navigate('/login')
        }}>Logout</span>
      ),
    },
  ]

    return(
        <div class="layout">
         <div className="header">
           <h1>Shaikh Resume</h1>
           <Dropdown menu={{ items, }} placement="bottomLeft" >
           <Button>{user.username}</Button>
           </Dropdown>
         </div>
         <div className="content">
           {props.children}
         </div>
        </div>
    )
}
export default DefaultLayout
